import { Store } from "../store";
const Message = {
    success: (message) => {
        Store.commit("setMessage", {
            message,
            color: "success",
        });
        Store.commit("setSnackbar", true);
    },
    danger: (message) => {
        Store.commit("setMessage", {
            message,
            color: "error",
        });
        Store.commit("setSnackbar", true);
    },
    warning: (message) => {
        Store.commit("setMessage", {
            message,
            color: "warning",
        });
        Store.commit("setSnackbar", true);
    },
    info: (message) => {
        Store.commit("setMessage", {
            message,
            color: "info",
        });
        Store.commit("setSnackbar", true);
    },
};
export default Message;
