import Message from "@/core/utils/Message";
export default {
    install(Vue) {
        Vue.prototype["$alert"] = {
            success(text) {
                return Message.success(text);
            },
            danger(text) {
                return Message.danger(text);
            },
            warning(text) {
                return Message.warning(text);
            },
            info(text) {
                return Message.info(text);
            }
        };
    }
};
